INSERT INTO `wp_comments` VALUES (320,2617,'Elyse Andrews','elyseandrews@gmail.com','','209.6.87.47','2012-11-19 14:21:59','2012-11-19 19:21:59','Thanks Courtney! I too am so happy to see great people starting fresh new businesses all over town. It\'s about time. :-)',0,'1','Mozilla/5.0 (Macintosh; Intel Mac OS X 10_7_5) AppleWebKit/537.4 (KHTML, like Gecko) Chrome/22.0.1229.79 Safari/537.4','',317,4);
